mapper.select:
SELECT 

FROM fea_fundssrc_b a

WHERE a.id = #{id}

SELECT 

FROM fea_fundssrc_b a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.pkfundssrc = #{fea_fundssrc.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM fea_fundssrc_b a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}

mapper.update:
DELETE FROM fea_fundssrc_b


		WHERE id = #{id}


		WHERE pkfundssrc = #{fea_fundssrc.id}

UPDATE fea_fundssrc_b SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE pkfundssrc = #{fea_fundssrc.id}
