mapper.update:
DELETE FROM fea_investdis_b


		WHERE id = #{id}


		WHERE pkinvestdis = #{fea_investdis.id}

UPDATE fea_investdis_b SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE pkinvestdis = #{fea_investdis.id}
